import { useState, useEffect } from 'react';
import logo from './lotus.png';
import './App.css';

function App() {
  const [isSticky, setSticky] = useState(false);

  const handleScroll = () => {
    // Set the sticky state based on scroll position
    setSticky(window.scrollY > 50); // Adjust the scroll value as needed
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  return (
    <div className="App">
      <div className="banner">
      <div className={isSticky ? "sticky-header" : "App-header"}>
          <h1>BetterBy.Science</h1>
        </div>
      <button className="login-button">Log In</button>
      </div>
      <header className="App-header">
      <img src={logo} alt="logo" style={{ width: '250px', height: '250px' }} /> {}
        <h1>BetterBy.Science</h1>
      </header>
      <div className="content-container">
      <body>
        <section>
          <h2>Welcome to Your Journey Towards Mindful Living</h2>
          <p>
            At the core of our philosophy lies a commitment to a holistic approach to wellness, combining the ancient wisdom of yoga with a modern understanding of physiology. Our platform is designed to nurture both mind and body, paving the way for maximum health and wellbeing.
          </p>
        </section>

        <section>
          <h2>Mindfulness: The Science of Inner Peace</h2>
          <p>
            Our mindfulness practices are grounded in scientific research, offering techniques that have been proven to reduce stress, enhance focus, and promote a balanced emotional state. From guided meditations to breathing exercises, each aspect of our program is crafted to help you cultivate a deep sense of inner peace.
          </p>
        </section>

        <section>
          <h2>Yoga: Harmonizing Body and Mind</h2>
          <p>
            Dive into the world of yoga with our expert-led sessions. Whether you're a beginner or a seasoned yogi, our app offers a wide range of yoga styles to suit your needs. Each pose and sequence is chosen for its efficacy in improving flexibility, strength, and body awareness, all while fostering mental clarity.
          </p>
        </section>

        <section>
          <h2>Personalized Coaching at Your Fingertips</h2>
          <p>
            What sets us apart is our personalized coaching approach. Our app connects you with experienced coaches who provide tailored guidance based on your individual goals and progress. With our science-based approach, every piece of advice you receive is rooted in proven methodologies to enhance your health and wellness journey.
          </p>
        </section>

        <section>
          <h2>Join Our Community</h2>
          <p>
            Embark on a transformative journey with us. Our community is waiting to welcome you into a world where mindful living and physical health go hand in hand. Download the app today and take the first step towards a healthier, more balanced you.
          </p>
        </section>
</body>
</div>
    </div>
  );
}

export default App;
